import React, { Component } from 'react';
import { CardList } from './components/card-list/card-list.component';
import './App.css';

class App extends Component { 
  constructor() {
    super();

    this.state = {
      monsters: []
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users') // paste .json url in the brackets!
    .then(response => response.json())
    .then(users => this.setState(({ monsters: users })));
  }


  render() {
    return ( 
      <div className="App">
      <CardList name='Darren'>  {this.state.monsters.map(monster => (
          <h1 key={monster.id}> { monster.name }</h1>
          ))}
      <h1>Darren</h1>
      </CardList> 

      </div>
    );
  }
}

export default App;
